-- Create a table with both GUID and INT columns\nCREATE TABLE Sessions (\n    SessionID INT IDENTITY(1,1) PRIMARY KEY,\n    SessionGUID UNIQUEIDENTIFIER DEFAULT NEWSEQUENTIALID(),\n    LastAccessTime DATETIME DEFAULT GETDATE(),\n    -- other session-related fields...\n);\n\n-- Ensure the GUID column is indexed for quick lookups\nCREATE UNIQUE INDEX IX_Sessions_SessionGUID ON Sessions(SessionGUID);