CREATE TABLE TypeA (\n    Id INT PRIMARY KEY,\n    ParentId_B INT NULL,  -- Foreign key to TypeB\n    ParentId_C NVARCHAR(255) NULL,  -- Foreign key to TypeC\n    ParentTypeId VARCHAR(50),  -- Optional: To specify the parent type if needed\n\n    FOREIGN KEY (ParentId_B) REFERENCES TypeB(Id),\n    FOREIGN KEY (ParentId_C) REFERENCES TypeC(Id),\n\n    CHECK (\n        (ParentId_B IS NOT NULL AND ParentId_C IS NULL)\n        OR\n        (ParentId_B IS NULL AND ParentId_C IS NOT NULL)\n    )\n);\n\nCREATE TABLE TypeB (\n    Id INT PRIMARY KEY,\n    -- other columns\n);\n\nCREATE TABLE TypeC (\n    Id NVARCHAR(255) PRIMARY KEY,  -- assuming string PK\n    -- other columns\n);