SELECT COUNT(d.day_date) AS empty_days_count\nFROM (\n    SELECT DATE_ADD(CURDATE(), INTERVAL (WEEKDAY(CURDATE()) - DAYOFWEEK(CURDATE()) + 2) % 7 + DAYOFWEEK(CURDATE()) - 8 + n DAY) day_date\n    FROM (SELECT 0 AS n UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6) numbers\n) d\nLEFT JOIN cali_dates e ON d.day_date = e.date AND YEARWEEK(e.date) = YEARWEEK(CURDATE())\nWHERE e.date IS NULL;