DECLARE @ProcedureName NVARCHAR(128) = 'YourStoredProcedureName';\n\nSET NOCOUNT ON;\n\nDECLARE db_cursor CURSOR FOR \nSELECT name FROM sys.databases WHERE state = 0 AND name NOT IN ('master', 'model', 'msdb', 'tempdb');\n\nDECLARE @dbname NVARCHAR(128);\nDECLARE @sql NVARCHAR(MAX);\n\nOPEN db_cursor;\n\nFETCH NEXT FROM db_cursor INTO @dbname;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SELECT @sql = N'USE [' + @dbname + N']; \n                    IF EXISTS (SELECT * FROM sys.procedures WHERE name LIKE ''%' + @ProcedureName + '%'')\n                    BEGIN\n                        PRINT ''Found in database: ' + @dbname + '''\n                    END';\n    \n    EXEC sp_executesql @sql;\n\n    FETCH NEXT FROM db_cursor INTO @dbname;\nEND \n\nCLOSE db_cursor;\nDEALLOCATE db_cursor;