-- Ensure the view adheres to indexed view requirements\nCREATE VIEW dbo.YourIndexedView\nWITH SCHEMABINDING\nAS\nSELECT \n    col1,\n    col2,\n    -- Ensure no subqueries or unsupported aggregate functions are used, or use them in a supported way.\n    COUNT_BIG(*) AS cnt  -- Example of a supported usage\nFROM \n    dbo.BaseTable\nGROUP BY \n    col1, col2;\nGO\n\n-- Create unique clustered index on the view\nCREATE UNIQUE CLUSTERED INDEX idx_view \nON dbo.YourIndexedView (col1, col2);