DECLARE @foo INT\nSET @foo = NULL\n\n-- Create the temporary table first\nCREATE TABLE #TempTable (\n    message NVARCHAR(50)\n)\n\n-- Check the value of @foo and insert accordingly\nIF (@foo IS NULL)\nBEGIN\n    INSERT INTO #TempTable (message) VALUES ('foo was null')\nEND\nELSE\nBEGIN\n    INSERT INTO #TempTable (message) VALUES ('foo not null')\nEND\n\n-- Example to retrieve data from temp table to check results\nSELECT * FROM #TempTable