CREATE PROCEDURE spProcessIntegerLists\n    @List1 NVARCHAR(500),\n    @List2 NVARCHAR(500)\nAS\nBEGIN\n    -- Parse each list using the SplitInts function\n    SELECT Id AS IntFromList1 INTO #ListOne FROM dbo.SplitInts(@List1, ',')\n\n    SELECT Id AS IntFromList2 INTO #ListTwo FROM dbo.SplitInts(@List2, ',')\n\n    -- Perform your operations on the lists\n    -- For example:\n    SELECT * FROM #ListOne\n    UNION ALL\n    SELECT * FROM #ListTwo\n\n    DROP TABLE #ListOne\n    DROP TABLE #ListTwo\nEND