-- Creating lookup table with CHAR(2) as primary key\nCREATE TABLE ProductLookup (\n    ProductCode CHAR(2) PRIMARY KEY,\n    ProductName VARCHAR(100)\n);\n\n-- Inserting data into lookup table\nINSERT INTO ProductLookup (ProductCode, ProductName) VALUES ('FB', 'Foo Bar'), ('AB', 'Another Product');\n\n-- Creating another table using the product code as foreign key\nCREATE TABLE Orders (\n    OrderID INT PRIMARY KEY,\n    ProductCode CHAR(2),\n    FOREIGN KEY (ProductCode) REFERENCES ProductLookup(ProductCode)\n);\n\n-- Inserting data into Orders table\nINSERT INTO Orders (OrderID, ProductCode) VALUES (1, 'FB');