CREATE OR REPLACE TRIGGER restrict_updates_trigger\nBEFORE UPDATE ON your_table\nFOR EACH ROW\nDECLARE\n  v_module VARCHAR2(32);\nBEGIN\n  -- Get the module information from v$session\n  SELECT s.module INTO v_module\n  FROM v$session s, v$mystat m\n  WHERE s.sid = m.sid AND m.statistic# = 1;\n\n  IF v_module IS NULL OR v_module != 'MY_APP_SESSION' THEN\n    RAISE_APPLICATION_ERROR(-20002, 'Updates to this table are restricted.');\n  END IF;\nEND;