-- Create Staging Tables\nCREATE TABLE Movies (\n    UniqueName NVARCHAR(255),\n    MovieTitle NVARCHAR(255),\n    YearOfProduction INT,\n    PRIMARY KEY (UniqueName)\n);\n\nCREATE TABLE MPPARatings (\n    UniqueName NVARCHAR(255),\n    MPARRating NVARCHAR(10),\n    RatingReason NVARCHAR(255),\n    PRIMARY KEY (UniqueName)\n);\n\nCREATE TABLE RunningTimes (\n    UniqueName NVARCHAR(255),\n    RunningTime INT,\n    PRIMARY KEY (UniqueName)\n);\n\n-- Bulk Insert Commands (adjust file paths and formats as necessary)\nBULK INSERT Movies\nFROM 'C:\path\to\movies.list'\nWITH (FORMATFILE = 'C:\path\to\movies.format');\n\nBULK INSERT MPPARatings\nFROM 'C:\path\to\mpaa-ratings-reasons.list'\nWITH (FORMATFILE = 'C:\path\to\rating.format');\n\nBULK INSERT RunningTimes\nFROM 'C:\path\to\running-times.list'\nWITH (FORMATFILE = 'C:\path\to\times.format');\n\n-- Query to join tables and filter out non-movie entries\nSELECT m.MovieTitle, m.YearOfProduction, mr.MPARRating, rt.RunningTime\nFROM Movies m\nJOIN MPPARatings mr ON m.UniqueName = mr.UniqueName\nJOIN RunningTimes rt ON m.UniqueName = rt.UniqueName\nWHERE m.UniqueName NOT LIKE '%(Video Game)%' AND m.UniqueName NOT LIKE '%(TV Series)%'