WITH DateRange AS (\n    SELECT MIN(date) + INTERVAL(n - 1) DAY AS gen_date\n    FROM (SELECT @n := @n + 1 as n FROM (SELECT 0 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) a, \n                 (SELECT 0 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) b, \n                 (SELECT @n := 0) AS init\n    ) numbers \n    WHERE gen_date BETWEEN '2023-11-05' AND '2023-17-05'\n)\nSELECT dr.gen_date, t.bID, t.sName\nFROM DateRange dr\nLEFT JOIN table1 t ON dr.gen_date = t.date AND t.fID = 'P1';