CREATE TABLE JobTypes (\n    JobTypeID INT PRIMARY KEY,\n    JobTypeName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE JobLocations (\n    JobLocationID INT PRIMARY KEY,\n    City VARCHAR(100),\n    State VARCHAR(100),\n    Zip VARCHAR(20)\n);\n\nCREATE TABLE JobPostings (\n    JobPostingID INT PRIMARY KEY,\n    UserID INT, -- This should reference a User/Members table\n    Company VARCHAR(255) NOT NULL,\n    JobTitle VARCHAR(255) NOT NULL,\n    JobTypeID INT,\n    JobLocationID INT,\n    JobDescription TEXT,\n    HowToApply TEXT,\n    CompanyURL VARCHAR(255),\n    LogoURL VARCHAR(255),\n    FOREIGN KEY (JobTypeID) REFERENCES JobTypes(JobTypeID),\n    FOREIGN KEY (JobLocationID) REFERENCES JobLocations(JobLocationID)\n);