CREATE TRIGGER tr_MyTable_Delete ON MyTable\nAFTER DELETE AS\nBEGIN\n    INSERT INTO AuditLog (TableName, ColumnName, ChangedFrom, ChangedTo, UpdatedBy)\n    SELECT \n        'MyTable',\n        col.name,\n        d.[Column],\n        NULL,\n        USER_NAME()\n    FROM deleted d\n    CROSS APPLY (\n        SELECT d.Column AS [Column]\n    ) t1\n    INNER JOIN sys.columns AS col ON 1=1; -- Log all columns on delete\nEND;