SELECT \n    bc.id, \n    bc.title, \n    bc.estimateCurrent, \n    IFNULL(SUM(TIME_TO_SEC(TIMEDIFF(t.dateEnding, t.dateBeginning))), 0) AS timeusedinsec\nFROM \n    cases bc\nLEFT JOIN \n    (SELECT `#case`, SUM(TIME_TO_SEC(TIMEDIFF(dateEnding, dateBeginning))) AS total_time\n     FROM timesheet\n     GROUP BY `#case`) t \nON \n    bc.id = t.`#case`\nGROUP BY \n    bc.id, \n    bc.title, \n    bc.estimateCurrent;