SELECT F.readings_miu_id, F.Reading, F.ReadDate, Format(F.MIUtime, 'hh:mm:ss') AS ReadTime,\n       F.MIUwindow, F.SN, F.Noise, F.RSSI, F.ColRSSI, F.MIURSSI, F.Firmware, F.CFGDate, \n       F.FreqCorr, F.Active, F.MeterType, F.OriginCol, F.ColID, F.Ownage, P.SiteID, \n       P.PremID, P.prem_group1, P.prem_group2, F.ReadID\nFROM Neptune_FN_Analysis AS F\nINNER JOIN Neptune_prem AS P ON F.PremID = P.premice_id\nWHERE F.Active = 'Y' AND F.OriginCol = 'US 29' AND F.ColID = 1 \n      AND F.SiteID = 36801 \n      AND F.ReadDate BETWEEN #4/21/2009# AND #4/29/2009#\nAND (F.readings_miu_id, F.ReadDate) IN (\n    SELECT AF.readings_miu_id, AF.ReadDate\n    FROM Neptune_FN_Analysis AS AF\n    WHERE AF.Active = 'Y' AND AF.OriginCol = 'US 29' AND AF.ColID = 1 \n          AND AF.SiteID = 36801 \n          AND AF.ReadDate BETWEEN #4/21/2009# AND #4/29/2009#\n    GROUP BY AF.readings_miu_id, AF.ReadDate\n)\nAND F.ReadTime = (\n    SELECT MAX(AF.ReadTime)\n    FROM Neptune_FN_Analysis AS AF\n    WHERE AF.readings_miu_id = F.readings_miu_id \n          AND AF.ReadDate = F.ReadDate\n);