-- Temporary table to hold category IDs\nCREATE TEMPORARY TABLE IF NOT EXISTS temp_categories (\n    category_id INT PRIMARY KEY\n);\n\n-- Insert the initial category ID into the temporary table\nINSERT INTO temp_categories (category_id) VALUES (10);\n\n-- Iteratively insert child and parent categories until no new ids are inserted\nSET @count = 1;\nWHILE @count > 0 DO\n    INSERT IGNORE INTO temp_categories (category_id)\n    SELECT c.category_id\n    FROM categories c\n    JOIN temp_categories tc ON c.parent_id = tc.category_id OR c.category_id = tc.parent_id;\n\n    SET @count = ROW_COUNT();\nEND WHILE;\n\n-- Query articles that match the category_ids in the temporary table\nSELECT a.*\nFROM articles a\nJOIN temp_categories tc ON a.category_id = tc.category_id;\n\n-- Drop the temporary table after use\nDROP TEMPORARY TABLE IF EXISTS temp_categories;