-- Declare variables for trace creation\nDECLARE @TraceID int;\nDECLARE @maxfilesize bigint = 10;   -- Maximum file size in MB\n\n-- Create a new trace with default settings\nEXEC sp_trace_create @TraceID output, 2, N'C:\SQLServerLogs\ActionTrace', @maxfilesize, NULL;\n\n-- Add an event to the trace for SQL:BatchCompleted (Event Class ID = 10)\nEXEC sp_trace_setevent @TraceID, 10, 14, 1;    -- Batch Text\nEXEC sp_trace_setevent @TraceID, 10, 35, 1;    -- Duration\nEXEC sp_trace_setevent @TraceID, 10, 12, 1;    -- CPU\nEXEC sp_trace_setevent @TraceID, 10, 8, 1;     -- HostName\nEXEC sp_trace_setevent @TraceID, 10, 37, 1;    -- ApplicationName\nEXEC sp_trace_setevent @TraceID, 10, 50, 1;    -- LoginName\n\n-- Set the filter to capture only DELETE operations (you may want to adjust this based on what you need)\nDECLARE @Filter int = 83;\nEXEC sp_trace_setfilter @TraceID, @Filter, 1, '=', N'DELETE';\n\n-- Start the trace\nEXEC sp_trace_start @TraceID;