-- Create a temporary table with the user_ids you need\nCREATE TEMPORARY TABLE temp_user_ids (user_id INT PRIMARY KEY);\n\n-- Insert needed user_ids into the temporary table\nINSERT INTO temp_user_ids (user_id) VALUES \n(32343), (45676), (12345), (98765), (66010); -- ...add all your user_ids here\n\n-- Query using a JOIN with the temporary table\nSELECT c.*\nFROM customers c\nJOIN temp_user_ids t ON c.user_id = t.user_id;\n\n-- Drop the temporary table after you're done\nDROP TABLE IF EXISTS temp_user_ids;