-- Table for standard locations\nCREATE TABLE Locations (\n    Floor INT,\n    Row INT,\n    Column INT,\n    Shelf INT,\n    MaxBoxes INT DEFAULT 21 -- Assuming most locations can store 21 boxes\n);\n\n-- Table for exceptions (locations with different box limits)\nCREATE TABLE Exceptions (\n    Floor INT,\n    Row INT,\n    Column INT,\n    Shelf INT,\n    MaxBoxes INT,\n    PRIMARY KEY (Floor, Row, Column, Shelf),\n    FOREIGN KEY (Floor, Row, Column, Shelf) REFERENCES Locations(Floor, Row, Column, Shelf)\n);\n\n-- Table for tracking scanned boxes\nCREATE TABLE Scans (\n    Floor INT,\n    Row INT,\n    Column INT,\n    Shelf INT,\n    BoxNumber INT,\n    ScanTime DATETIME DEFAULT GETDATE()\n);