CREATE FUNCTION dbo.SplitStringToTable (@Input NVARCHAR(MAX), @Delimiter CHAR(1))\nRETURNS @Output TABLE (Value NVARCHAR(255))\nAS\nBEGIN\n    DECLARE @Start INT, @End INT\n    SET @Start = 1\n    SET @End = CHARINDEX(@Delimiter, @Input)\n\n    WHILE @Start < LEN(@Input) + 1 BEGIN\n        IF @End = 0 \n            SET @End = LEN(@Input) + 1\n\n        INSERT INTO @Output (Value)\n        VALUES (SUBSTRING(@Input, @Start, @End - @Start))\n\n        SET @Start = @End + 1\n        SET @End = CHARINDEX(@Delimiter, @Input, @Start)\n    END\n\n    RETURN\nEND