CREATE PROCEDURE InsertPersonData\n    @personID INT,\n    @name NVARCHAR(100),\n    @type CHAR(1)  -- 'T' for Teacher, 'S' for Student, etc.\nAS\nBEGIN\n    IF @type = 'T'\n        INSERT INTO dbo.People (PersonID, Name)\n        VALUES (@personID, @name);\n    ELSE IF @type = 'S'\n        INSERT INTO LINKEDSERVERNAME.DatabaseName.dbo.People (PersonID, Name)\n        VALUES (@personID, @name);\n    -- Add cases for other types as necessary\nEND;