CREATE OR REPLACE FUNCTION replace_control_chars(input_text IN CLOB) RETURN CLOB IS\n    v_output CLOB := input_text;\nBEGIN\n    FOR i IN 0 .. 31 LOOP\n        CASE i\n            WHEN 0 THEN v_output := REPLACE(v_output, CHR(i), '\x00');\n            WHEN 9 THEN v_output := REPLACE(v_output, CHR(i), '\t');\n            WHEN 10 THEN v_output := REPLACE(v_output, CHR(i), '\n');\n            ELSE v_output := REPLACE(v_output, CHR(i), '');\n        END CASE;\n    END LOOP;\n    RETURN v_output;\nEND replace_control_chars;\n/