DECLARE @TimeKey INT = 1;\nDECLARE @CurrentDateTime DATETIME2 = '2023-10-06 08:00:00';\nINSERT INTO DimTime (TimeKey, FullDateTime, Hour, DayOfWeekName, DayOfMonth, MonthOfYear, Year, IsWeekend)\nVALUES (\n    @TimeKey,\n    @CurrentDateTime,\n    DATEPART(HOUR, @CurrentDateTime),\n    FORMAT(@CurrentDateTime, 'dddd'),\n    DATEPART(DAY, @CurrentDateTime),\n    DATEPART(MONTH, @CurrentDateTime),\n    DATEPART(YEAR, @CurrentDateTime),\n    CASE WHEN DATENAME(WEEKDAY, @CurrentDateTime) IN ('Saturday', 'Sunday') THEN 1 ELSE 0 END\n);