DECLARE @BatchSize INT = 1000;\n    DECLARE @Offset INT = 0;\n    DECLARE @Count INT;\n\n    WHILE EXISTS (SELECT 1 FROM #IDs)\n    BEGIN\n        UPDATE TOP (@BatchSize) y\n        SET y.SomeColumn = 'NewValue'\n        FROM YourTable y\n        JOIN (\n            SELECT ID FROM #IDs \n            ORDER BY ID OFFSET @Offset ROWS FETCH NEXT @BatchSize ROWS ONLY\n        ) i ON y.ID = i.ID;\n\n        DELETE TOP (@BatchSize) FROM #IDs;\n    END;