CREATE PROCEDURE InsertDateExample\n    @Year INT,\n    @Month INT,\n    @Day INT\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @InputDate DATE;\n    BEGIN TRY\n        -- Combine the parts into a date, which will fail if they don't form a valid date\n        SET @InputDate = DATEFROMPARTS(@Year, @Month, @Day);\n\n        -- Proceed with using @InputDate in your logic here\n        PRINT 'Valid Date: ' + CONVERT(VARCHAR(10), @InputDate, 120);\n    END TRY\n    BEGIN CATCH\n        -- Handle the error if DATEFROMPARTS fails to create a valid date\n        DECLARE @ErrorMessage NVARCHAR(4000);\n        DECLARE @ErrorSeverity INT;\n        DECLARE @ErrorState INT;\n\n        SELECT \n            @ErrorMessage = ERROR_MESSAGE(),\n            @ErrorSeverity = ERROR_SEVERITY(),\n            @ErrorState = ERROR_STATE();\n\n        RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState);\n    END CATCH\nEND