WITH Group1 AS (\n    SELECT DISTINCT pp.product_id\n    FROM products_ingridientproductbound pp\n    WHERE pp.ingridient_id IN (16, 17, 18, 19)\n),\nGroup2 AS (\n    SELECT DISTINCT pp.product_id\n    FROM products_ingridientproductbound pp\n    WHERE pp.ingridient_id IN (43, 44, 45)\n)\nSELECT p.id, p.name\nFROM products_product p\nJOIN Group1 g1 ON p.id = g1.product_id\nJOIN Group2 g2 ON p.id = g2.product_id\nLIMIT 21;