DECLARE @seed INT = 1000;\n\nCREATE TABLE #temp (\n    id INT,\n    [date] DATETIME\n);\n\nINSERT INTO #temp (id, [date]) VALUES \n(1, '20090119'), \n(2, '20090118'), \n(3, '20090117'), \n(4, '20090116'), \n(5, '20090115'), \n(6, '20090114');\n\n-- Use HASHBYTES to create a consistent random number based on id and seed\nSELECT *, \n       CAST(HASHBYTES('SHA1', CAST(id AS VARCHAR) + '_' + CAST(@seed AS VARCHAR)) AS BIGINT) % 1000 AS r\nFROM #temp\nORDER BY r;\n\nDROP TABLE #temp;