UPDATE warehouse_box wb\nSET warehouse_box_status_id = wbsv.warehouse_box_status_id\nFROM warehouse_box_status_vw wbsv \nINNER JOIN pallet_warehouse_box pwb ON wbsv.warehouse_box_id = pwb.warehouse_box_id \nINNER JOIN routing_shipment_pallet rsp ON pwb.pallet_id = rsp.pallet_id\nWHERE wb.id = wbsv.warehouse_box_id  -- Ensure this column matches the primary key or unique identifier in warehouse_box\nAND rsp.date_removed IS NULL\nAND pwb.date_unpalletized IS NULL\nAND rsp.routing_shipment_id = 100002;