-- Creating a View with some conditions and computations\nCREATE VIEW UserActive AS\nSELECT \n    UserID, \n    FirstName, \n    LastName, \n    Status,\n    DATEDIFF(year, BirthDate, GETDATE()) AS Age -- Computed column based on logic\nFROM \n    IdentitySystem.dbo.Users\nWHERE \n    Status = 'Active'; -- Security/data filtering\n\n-- Creating a Synonym for direct reference with no changes to underlying data structure or access\nCREATE SYNONYM UsersSyn FOR IdentitySystem.dbo.Users;