SELECT \n   u.name AS schema_name,\n   o.name AS object_name,\n   CASE \n       WHEN o.type = 'S' THEN 'SYSTEM TABLE'\n       ELSE 'TABLE'\n   END AS table_type,\n   (\n       SELECT CONVERT(VARCHAR(8000), value)\n       FROM ::fn_listextendedproperty(NULL, 'schema', u.name, 'table', o.name, NULL, NULL) \n       WHERE name = 'MS_Description'\n   ) AS MS_Description\nFROM \n   sys.objects o\nJOIN \n   sys.schemas  u ON (u.schema_id = o.schema_id)\nWHERE \n   o.type IN ('U', 'S')\n   AND u.name = N'something here' -- Use N for Unicode string if necessary\nORDER BY \n   schema_name, \n   object_name;