DECLARE @p1 VARCHAR(50);\nDECLARE @utcDateTime DATETIME;\nDECLARE @dateTimePart DATETIME;\nDECLARE @offsetHours INT;\n\nSET @p1 = '2009-06-26 14:30:00.000Z-4:00'; -- could be ISO8601\n\n-- Extract the date and time part\nSET @dateTimePart = LEFT(@p1, LEN(@p1) - 6);\n\n-- Extract the offset in hours (assuming always in Â±H:MM format)\nIF CHARINDEX('+', @p1) > 0\n    SET @offsetHours = CONVERT(INT, SUBSTRING(@p1, CHARINDEX('+', @p1) + 1, 2));\nELSE IF CHARINDEX('-', @p1) > 0\n    SET @offsetHours = -CONVERT(INT, SUBSTRING(@p1, CHARINDEX('-', @p1) + 1, 2));\n\n-- Convert to UTC by adding the offset (negative in this case means we need to add)\nSET @utcDateTime = DATEADD(HOUR, DATEDIFF(HOUR, GETUTCDATE(), GETDATE()) + @offsetHours, CAST(@dateTimePart AS DATETIME));\n\nSELECT @utcDateTime; -- should yield "2009-06-26 18:30:00.000"