-- For MySQL:\nSELECT DAYNAME('2009-05-01') AS DayOfWeek;\nSELECT DAYNAME('2009-05-12') AS DayOfWeek;\n\n-- For SQL Server:\nSELECT DATENAME(WEEKDAY, '2009-05-01') AS DayOfWeek;\nSELECT DATENAME(WEEKDAY, '2009-05-12') AS DayOfWeek;\n\n-- For PostgreSQL:\nSELECT TO_CHAR('2009-05-01'::date, 'Day') AS DayOfWeek;\nSELECT TO_CHAR('2009-05-12'::date, 'Day') AS DayOfWeek;\n\n-- For Oracle:\nSELECT TO_CHAR(TO_DATE('2009-05-01', 'YYYY-MM-DD'), 'Day') AS DayOfWeek FROM dual;\nSELECT TO_CHAR(TO_DATE('2009-05-12', 'YYYY-MM-DD'), 'Day') AS DayOfWeek FROM dual;\n\n-- For SQLite:\nSELECT CASE strftime('%w', '2009-05-01')\n    WHEN '0' THEN 'Sunday'\n    WHEN '1' THEN 'Monday'\n    WHEN '2' THEN 'Tuesday'\n    WHEN '3' THEN 'Wednesday'\n    WHEN '4' THEN 'Thursday'\n    WHEN '5' THEN 'Friday'\n    ELSE 'Saturday'\nEND AS DayOfWeek;\nSELECT CASE strftime('%w', '2009-05-12')\n    WHEN '0' THEN 'Sunday'\n    WHEN '1' THEN 'Monday'\n    WHEN '2' THEN 'Tuesday'\n    WHEN '3' THEN 'Wednesday'\n    WHEN '4' THEN 'Thursday'\n    WHEN '5' THEN 'Friday'\n    ELSE 'Saturday'\nEND AS DayOfWeek;