CREATE OR REPLACE TRIGGER check_client_ip_trigger \nBEFORE LOGON ON DATABASE\nDECLARE  \n   v_allowed_ip VARCHAR2(15) := '192.168.0.1'; -- Allowed IP address\n   v_client_ip  VARCHAR2(40);\nBEGIN\n   v_client_ip := SYS_CONTEXT('USERENV', 'IP_ADDRESS');\n   IF (SYS_CONTEXT ('USERENV','SESSION_USER') = 'YOUR_ORACLE_USER' AND v_client_ip != v_allowed_ip) THEN\n      RAISE_APPLICATION_ERROR(-20001, 'Connection not allowed from this IP address.');\n   END IF;\nEND;