CREATE PROCEDURE TestProcedureWithTimeout\nAS\nBEGIN\n    -- Set timeout for the following queries to 60 seconds\n    SET QUERY_TIMEOUT 60;\n\n    BEGIN TRY\n        -- Your query here\n        SELECT * FROM MyTable;\n        PRINT 'Statement completed in less than 60 seconds';\n    END TRY\n    BEGIN CATCH\n        IF ERROR_NUMBER() = -2 -- Error number for timeout\n            PRINT 'Statement timed out after 60 seconds';\n        ELSE\n            PRINT 'An error occurred: ' + ERROR_MESSAGE();\n    END CATCH\nEND