-- First, create the Table Type\nCREATE TYPE dbo.ParameterList AS TABLE (\n    ParameterID INT,\n    ParameterValue NVARCHAR(255)\n);\n\n-- Then use this type in your Stored Procedure\nCREATE PROCEDURE dbo.ProcessParameters\n    @Params dbo.ParameterList READONLY\nAS\nBEGIN\n    -- Example query using the table-valued parameter\n    SELECT * FROM OtherTable \n    WHERE ID IN (SELECT ParameterID FROM @Params);\nEND;