DECLARE @ColumnA DataType, @ColumnB DataType;\n\nDECLARE RowCursor CURSOR FOR \nSELECT columnA, columnB\nFROM table1;\n\nOPEN RowCursor;\nFETCH NEXT FROM RowCursor INTO @ColumnA, @ColumnB;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    INSERT INTO table2 (column1, column2)\n    VALUES (@ColumnA, @ColumnB);\n\n    FETCH NEXT FROM RowCursor INTO @ColumnA, @ColumnB;\nEND;\n\nCLOSE RowCursor;\nDEALLOCATE RowCursor;