-- Template for generating a user-specific materialized view\nCREATE OR REPLACE FUNCTION create_user_specific_view(\n    target_table TEXT,\n    owner_column TEXT DEFAULT 'owner',\n    new_view_name TEXT DEFAULT NULL\n) RETURNS VOID LANGUAGE plpgsql AS $$\nBEGIN\n    IF new_view_name IS NULL THEN\n        new_view_name := format('%I_user_view', target_table);\n    END IF;\n\n    EXECUTE format('CREATE MATERIALIZED VIEW %I AS\n                   SELECT * FROM %I WHERE %I = CURRENT_USER',\n                   new_view_name, target_table, owner_column);\n\n    -- Optional: Create an index for performance if needed\n    EXECUTE format('CREATE INDEX ON %I (%I)', new_view_name, 'id');\nEND;\n$$;\n\n-- Example usage\nSELECT create_user_specific_view('quizzes');\nSELECT create_user_specific_view('exams', owner_column => 'creator_id');