USE [YourPublicationDatabase];\n    GO\n\n    EXEC sp_addarticle \n        @publication = N'YourPublication',\n        @article = N'NewArticleName',\n        @source_owner = N'dbo',\n        @source_object = N'NewTableNameOrStoredProcedure', \n        @type = N'logbased view',  -- or 'proc schema only' if it's a stored procedure\n        @description = N'Adding new table',\n        @schema_option = 0x80050F3,  -- this includes schema changes\n        @sync_type = N'native';\n\n    EXEC sp_refreshsubscriptions \n         @publication = N'YourPublication'; \n\n    -- Generate a new snapshot\n    EXEC sp_startpublication_snapshot \n         @publication = N'YourPublication';