DECLARE @columnToSearch VARCHAR(128) = 'YourColumnName'; \nDECLARE @valueToFind INT = 123; -- The ID or value you're searching for\n\nSELECT TABLE_SCHEMA, TABLE_NAME\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE COLUMN_NAME = @columnToSearch\nAND (DATA_TYPE = 'int' OR DATA_TYPE = 'bigint') -- Adjust data type as needed\nINTO #TempTables;\n\nDECLARE @sql NVARCHAR(MAX) = '';\nDECLARE table_cursor CURSOR FOR \nSELECT TABLE_SCHEMA, TABLE_NAME FROM #TempTables;\nOPEN table_cursor;\nDECLARE @schema SYSNAME, @table SYSNAME;\n\nFETCH NEXT FROM table_cursor INTO @schema, @table;\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = 'IF EXISTS (SELECT 1 FROM [' + QUOTENAME(@schema) +'].['+ QUOTENAME(@table) +'] WHERE [' + QUOTENAME(@columnToSearch) + '] = @value)\n                 PRINT ''Table: [' + QUOTENAME(@schema) +'].['+ QUOTENAME(@table) +'];''';\n    EXEC sp_executesql @sql, N'@value INT', @value = @valueToFind;\n    FETCH NEXT FROM table_cursor INTO @schema, @table;\nEND\nCLOSE table_cursor;\nDEALLOCATE table_cursor;\n\nDROP TABLE #TempTables;