CREATE TABLE Countries (\n    CountryID INT PRIMARY KEY AUTO_INCREMENT,\n    CountryName VARCHAR(255) UNIQUE NOT NULL,\n    ISO3Code CHAR(3) UNIQUE NOT NULL -- ISO 3166-1 alpha-3 code for countries\n);\n\nCREATE TABLE States (\n    StateID INT PRIMARY KEY AUTO_INCREMENT,\n    StateName VARCHAR(255) NOT NULL,\n    CountryID INT,\n    FOREIGN KEY (CountryID) REFERENCES Countries(CountryID)\n);\n\nCREATE TABLE Cities (\n    CityID INT PRIMARY KEY AUTO_INCREMENT,\n    CityName VARCHAR(255) NOT NULL,\n    StateID INT,\n    FOREIGN KEY (StateID) REFERENCES States(StateID)\n);\n\nCREATE TABLE Streets (\n    StreetID INT PRIMARY KEY AUTO_INCREMENT,\n    StreetName VARCHAR(255) NOT NULL,\n    CityID INT,\n    FOREIGN KEY (CityID) REFERENCES Cities(CityID)\n);\n\nCREATE TABLE Addresses (\n    AddressID INT PRIMARY KEY AUTO_INCREMENT,\n    StreetNumber VARCHAR(100),\n    SuiteOrApartment VARCHAR(100),\n    StreetID INT,\n    PostalCode VARCHAR(20),\n    FOREIGN KEY (StreetID) REFERENCES Streets(StreetID)\n);