DECLARE\n    v_table_name VARCHAR2(30) := 'YOUR_TABLE_NAME'; -- Replace 'YOUR_TABLE_NAME' with your actual table name\n    v_output_file UTL_FILE.FILE_TYPE;\nBEGIN\n    -- Open an output file in the directory specified, which should be created before running this script\n    -- The user should have necessary privileges to read/write in that directory\n    v_output_file := UTL_FILE.FOPEN('MY_DIR', v_table_name || '_data_inserts.sql', 'W');\n\n    FOR rec IN (EXECUTE IMMEDIATE 'SELECT * FROM ' || v_table_name) LOOP\n        DECLARE\n            v_sql VARCHAR2(32767);\n        BEGIN\n            -- Construct the INSERT statement\n            SELECT RPAD('INSERT INTO ' || v_table_name, 50)\n                   || '(' ||\n                   RTRIM(XMLAGG(XMLELEMENT(e, COLUMN_NAME || ','))\n                             .GETCLOBVAL(), ',') ||\n                   ') VALUES (' ||\n                   RTRIM(XMLAGG(CASE WHEN DATA_TYPE IN ('NUMBER', 'FLOAT')\n                                     THEN XMLELEMENT(e, COLUMN_VALUE || ',').EXTRACT('//text()').GETCLOBVAL()\n                                      WHEN DATA_TYPE = 'DATE'\n                                     THEN XMLELEMENT(e, '''' || TO_CHAR(COLUMN_VALUE, 'YYYY-MM-DD HH24:MI:SS') || ''' , ').EXTRACT('//text()').GETCLOBVAL()\n                                      ELSE XMLELEMENT(e, '''' || REGEXP_REPLACE(REGEXP_REPLACE(COLUMN_VALUE, '''', ''''''), CHR(10), '') || ''' , ').EXTRACT('//text()').GETCLOBVAL()\n                                 END)\n                             .GETCLOBVAL(), ',') ||\n                   ');'\n            INTO v_sql\n            FROM ALL_TAB_COLUMNS\n            WHERE TABLE_NAME = UPPER(v_table_name)\n              AND COLUMN_ID <= rec.COLUMN_VALUE.count;\n\n            -- Write the INSERT statement to the file\n            UTL_FILE.PUT_LINE(v_output_file, v_sql);\n        END;\n    END LOOP;\n\n    -- Close the output file\n    UTL_FILE.FCLOSE(v_output_file);\n\nEXCEPTION\n    WHEN OTHERS THEN\n        DBMS_OUTPUT.PUT_LINE('Error: ' || SQLERRM);\n        IF UTL_FILE.IS_OPEN(v_output_file) THEN\n            UTL_FILE.FCLOSE(v_output_file);\n        END IF;\nEND;\n/