CREATE TABLE UserStories (\n    ID INT PRIMARY KEY IDENTITY(1,1),\n    Title NVARCHAR(MAX),\n    Url NVARCHAR(MAX),\n    UniqueName NVARCHAR(MAX) UNIQUE,\n    CategoryID INT,\n    FOREIGN KEY (CategoryID) REFERENCES Categories(ID)\n);\n\n-- Set UniqueName as the clustered index\nCREATE CLUSTERED INDEX idx_UniqueName ON UserStories(UniqueName);\n\n-- Create a nonclustered index on CategoryID\nCREATE NONCLUSTERED INDEX idx_CategoryID ON UserStories(CategoryID);