-- Assuming @analysisIds is a table-valued parameter or a temporary table with the analysis_ids to compare\nSELECT \n    alternative_id, \n    indicator_id,\n    GROUP_CONCAT(rank ORDER BY rank SEPARATOR ', ') AS ranks\nFROM \n    rankings\nWHERE \n    analysis_id IN (1, 2) -- Use your list of analysis IDs here\nGROUP BY \n    alternative_id, \n    indicator_id\nHAVING \n    COUNT(DISTINCT rank) > 1;