DECLARE @SchemaName NVARCHAR(128) = 'dbo' -- Change if different\nDECLARE @TableName NVARCHAR(128)\nDECLARE @IndexName NVARCHAR(128)\nDECLARE @SQLCommand NVARCHAR(MAX)\n\nDECLARE cur CURSOR FOR \nSELECT TABLE_NAME, INDEX_NAME \nFROM INFORMATION_SCHEMA.STATISTICS \nWHERE TABLE_SCHEMA = @SchemaName \nAND TABLE_TYPE = 'BASE TABLE' \nAND INDEX_NAME NOT IN (\n    SELECT name FROM sysindexes WHERE indid <= 1 OR (indid BETWEEN 256 AND 259)\n)\n\nOPEN cur\nFETCH NEXT FROM cur INTO @TableName, @IndexName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQLCommand = 'DROP INDEX ' + QUOTENAME(@SchemaName) + '.' + QUOTENAME(@TableName) + '.' + QUOTENAME(@IndexName) + ';'\n    PRINT @SQLCommand -- Optionally execute by replacing PRINT with EXEC\n\n    FETCH NEXT FROM cur INTO @TableName, @IndexName\nEND\n\nCLOSE cur\nDEALLOCATE cur