DECLARE\n  ln_count NUMBER;\nBEGIN\n  -- Original query without ROWNUM filter\n  SELECT 1 \n  INTO ln_count\n  FROM table_1, table_2, ..., table_n\n  WHERE <various joins and conditions>\n  AND ROWNUM = 1;\n\n  IF ln_count = 1 THEN\n    -- Do stuff if a row exists\n  END IF;\nEXCEPTION\n  WHEN NO_DATA_FOUND THEN\n    -- Handle the case where no data is found, as ln_count won't be assigned.\n    NULL; -- or your custom error handling code\nEND;