CREATE TRIGGER trg_EnsureBillableDateNotNull\nON your_table_name\nFOR INSERT, UPDATE\nAS\nBEGIN\n    IF EXISTS (\n        SELECT 1 \n        FROM inserted\n        WHERE billable = 1 AND (billabledate IS NULL OR billabledate = '')\n    )\n    BEGIN\n        RAISERROR ('If Billable is set to true, then BillableDate cannot be null.', 16, 1);\n        ROLLBACK TRANSACTION;\n    END\nEND