CREATE TABLE IF NOT EXISTS `joe`.`products_to_categories` (\n  `product_to_category_id` INT NOT NULL AUTO_INCREMENT,\n  `category_id` INT NOT NULL,\n  `product_id` INT NOT NULL,\n  PRIMARY KEY (`product_to_category_id`),\n  INDEX `idx_products_to_categories_category_id` (`category_id` ASC),\n  INDEX `idx_products_to_categories_product_id` (`product_id` ASC),\n  CONSTRAINT `fk_products_to_categories_category_id`\n    FOREIGN KEY (`category_id`)\n    REFERENCES `joe`.`categories` (`category_id`)\n    ON DELETE CASCADE\n    ON UPDATE NO ACTION,\n  CONSTRAINT `fk_products_to_categories_product_id`\n    FOREIGN KEY (`product_id`)\n    REFERENCES `joe`.`products` (`product_id`)\n    ON DELETE CASCADE\n    ON UPDATE NO ACTION)\nENGINE = InnoDB;