UPDATE Oildatasetstatus\nSET Oildatasetstatusid = CASE \n    WHEN Oildatasetstatus.Oildatasetstatusid = 5 THEN 16\n    WHEN Oildatasetstatus.Oildatasetstatusid = 6 THEN 17\n    WHEN Oildatasetstatus.Oildatasetstatusid IN (7, 8, 9) THEN 18\n    WHEN Oildatasetstatus.Oildatasetstatusid = 10 THEN 19\n    WHEN Oildatasetstatus.Oildatasetstatusid = 11 THEN 20\nEND\nWHERE EXISTS (\n    SELECT 1 \n    FROM oildataset \n    JOIN samplepoint ON oildataset.samplepointid = samplepoint.samplepointid\n    WHERE oildataset.oillabid = 4\n      AND oildataset.Oildatasetid = Oildatasetstatus.Oildatasetid\n);