-- Find rows that are in t1 but not in t2\nSELECT * FROM t1 LEFT JOIN t2 USING (id) WHERE t2.id IS NULL;\n\n-- Find rows that are in t2 but not in t1\nSELECT * FROM t2 LEFT JOIN t1 USING (id) WHERE t1.id IS NULL;\n\n-- Find rows where the data is different between t1 and t2\nSELECT *\nFROM t1 \nJOIN t2 ON t1.id = t2.id\nWHERE NOT (t1.column1 = t2.column1 AND t1.column2 = t2.column2 -- continue for all columns);