SELECT ac1.column_name AS ForeignKeyField,\n       ac2.column_name AS PrimaryKeyField,\n       aco.table_name  AS PrimaryTable\nFROM   all_constraints c1\nJOIN   all_cons_columns ac1 ON c1.constraint_name = ac1.constraint_name\nJOIN   all_constraints c2 ON c1.r_constraint_name = c2.constraint_name\nJOIN   all_cons_columns ac2 ON c2.constraint_name = ac2.constraint_name\nJOIN   all_objects aco ON c2.table_object_id = aco.object_id\nWHERE  c1.table_name = :name\nAND    c1.constraint_type = 'R';